def hex_to_wasm_file(hex_string: str, output_filename: str = "output.wasm"):
    cleaned_hex = hex_string.replace("\n", "").replace(" ", "")
    
    wasm_bytes = bytes.fromhex(cleaned_hex)
    
    with open(output_filename, "wb") as wasm_file:
        wasm_file.write(wasm_bytes)
        
        

def string_to_hex(input_string: str) -> str:
    output = int(input_string)

    output_bytes = hex(output)[2:]
    output_bytes = "00" + str(output_bytes)

    return output_bytes


if __name__ == "__main__":
    hex_string = ""
    output_filename = "output.wasm" 

    print(string_to_hex(hex_string))
 
    hex_to_wasm_file(string_to_hex(hex_string), output_filename)
